const ToogleContent=(button,closeContent,content,classContent)=>{if(button){button.addEventListener('click',()=>{content.classList.add(classContent)})}
if(closeContent){closeContent.addEventListener('click',()=>{content.classList.remove(classContent)})}};const searchButton=document.getElementById('search-button');const searchClose=document.getElementById('search-close');const searchContent=document.getElementById('search-content');const classContent='show-search';ToogleContent(searchButton,searchClose,searchContent,classContent);const shadowHeader=()=>{const header=document.getElementById('header');this.scrollY>=50?header.classList.add('shadow-header'):header.classList.remove('shadow-header')};window.addEventListener('scroll',shadowHeader);let swiperHome=new Swiper('.home__swiper',{loop:!0,spaceBetween:-24,grabCursor:!0,slidesPerView:'auto',centeredSlides:'auto',autoplay:{delay:3000,disableOnInteraction:!1,},breakpoints:{1220:{spaceBetween:-32,},},});let swiperFeatured=new Swiper('.featured__swipper',{loop:!0,spaceBetween:16,grabCursor:!0,slidesPerView:'auto',centeredSlides:'auto',navigation:{nextEl:'.swiper-button-next',prevEl:'.swiper-button-prev',},breakpoints:{1150:{slidesPerView:4,centeredSlides:!1,},},});let swiperNew=new Swiper('.new__swiper',{loop:!0,spaceBetween:16,slidesPerView:'auto',breakpoints:{1024:{slidesPerView:3,},},pagination:{el:'.swiper-pagination',dynamicBullets:!0,},});const scrollUp=()=>{const scrollProgress=document.getElementById('progress');const topPosition=document.documentElement.scrollTop;const scrollHeight=document.documentElement.scrollHeight;const clientHeight=document.documentElement.clientHeight;const calcHeight=scrollHeight-clientHeight;const scrollValue=Math.round((topPosition*100)/calcHeight);this.scrollY>=350?scrollProgress.classList.add('show-scroll'):scrollProgress.classList.remove('show-scroll');scrollProgress.addEventListener('click',()=>{document.documentElement.scrollTop=0});scrollProgress.style.background=`conic-gradient(#60678580 ${scrollValue}%, #d7d7d7 ${scrollValue}%)`};window.addEventListener('scroll',scrollUp);const themeButton=document.getElementById('theme-button');const darkTheme='dark-theme';const iconTheme='ri-sun-line';const selectedTheme=localStorage.getItem('selected-theme');const selectedIcon=localStorage.getItem('selected-icon');const getCurrentTheme=()=>document.body.classList.contains(darkTheme)?'dark':'light';const getCurrentIcon=()=>themeButton.classList.contains(iconTheme)?'ri-moon-clear-line':'ri-sun-line';if(selectedTheme){document.body.classList[selectedTheme==='dark'?'add':'remove'](darkTheme);themeButton.classList[selectedIcon==='ri-moon-clear-line'?'add':'remove'](iconTheme)}
themeButton.addEventListener('click',()=>{document.body.classList.toggle(darkTheme);themeButton.classList.toggle(iconTheme);localStorage.setItem('selected-theme',getCurrentTheme());localStorage.setItem('selected-icon',getCurrentIcon())});const sr=ScrollReveal({origin:'top',distance:'60px',duration:2500,delay:400,});sr.reveal(`.home__data, .featured__container, .new__container, .newsletter__container, .footer`);sr.reveal(`.home__images`,{delay:500});sr.reveal(`.services__card`,{interval:250});sr.reveal(`.discount__data`,{origin:'left'});sr.reveal(`.discount__images`,{origin:'right'});sr.reveal(`.left`,{origin:'right'});const showMenu=(toggleId,navId)=>{const toggle=document.getElementById(toggleId);const nav=document.getElementById(navId);toggle.addEventListener('click',()=>{nav.classList.toggle('show-menu');toggle.classList.toggle('show-close')})};showMenu('nav-toggle','nav-menu');const dropdownItems=document.querySelectorAll('.dropdown-item');dropdownItems.forEach((item)=>{const dropdownButton=item.querySelector('.down-link');dropdownButton.addEventListener('click',()=>{const showDropdown=document.querySelector('.show-dropdown');toggleItem(item);if(showDropdown!=null&&showDropdown!=item){toggleItem(showDropdown)}})});const toggleItem=(item)=>{const dropdownContainer=item.querySelector('.dropdown-container');if(item.classList.contains('show-dropdown')){dropdownContainer.removeAttribute('style');item.classList.remove('show-dropdown')}else{dropdownContainer.style.height=dropdownContainer.scrollHeight+'px';item.classList.add('show-dropdown')}};const mediaQuery=matchMedia('(min-width: 1024px)'),dropdownContainer=document.querySelectorAll('.dropdown-container');const removeStyle=()=>{if(mediaQuery.matches){dropdownContainer.forEach((e)=>{e.removeAttribute('style')});dropdownItems.forEach((e)=>{e.classList.remove('show-dropdown')})}};addEventListener('resize',removeStyle);const form=document.querySelector('.newsletter__form');form.addEventListener('submit',(e)=>{e.preventDefault();const data=Object.fromEntries(new FormData(e.target));alert(JSON.stringify(data))})
